mapper.select:
SELECT id, user_id, title, content, category, created_at, updated_at
FROM prompts
WHERE id = #{id}

mapper.update:
UPDATE prompts
SET title = #{title},
    content = #{content},
    category = #{category},
    updated_at = #{updatedAt}
WHERE id = #{id}
  AND user_id = #{userId}

mapper.delete:
DELETE FROM prompts
WHERE id = #{id}
  AND user_id = #{userId}
